    super(props)
    this.state={
      bborderEmail: '1px solid #999',
      bborderPassword: '1px solid #999',
      hover: 'false',
      nobitex_price:0,
      binance_price: 0,
      exir_price:0,
      nobitex_name: null,
      binance_name: null,
      Password: null,
      error: {
        email: null,
        password: null
      }
    }
  }

  componentDidMount () {
    this.interval = setInterval(() => {
      this.handleMax()
    }, 3000)
    }

  handleMax(){

    this.setState({ max: 0 })
    this.setState({ min: 0 })
    axios.get('https://api.exir.io/v1/trades', {
    })
      .then(response => {
        var price= Number.parseInt(response.data["btc-usdt"][0].price, 10)
        var name = 'nobitex_bitcoin'
        if (price>Number.parseInt(this.state.max, 10)){
          this.setState({ max: response.data["btc-usdt"][0].price })
          this.setState({ name_max: name })  
        };
        if (price<Number.parseInt(this.state.max, 10)){
          this.setState({ min: response.data["btc-usdt"][0].price }) 
          this.setState({ name_min: name })
        };
		})
      .catch(error => {
        console.log('error:')
      })

    let data = {
      symbol: "BTCUSDT"
    }

    axios.post('https://api.nobitex.ir/v2/trades', data,{})
    .then((response) => {
      var nobitex_price= Number.parseInt(response.data.trades[0].price, 10)
      var nobitex_name = 'nobitex_bitcoin'
      console.log('nobitex_bitcoin', response.data.trades[0].price , "    time",response.data.trades[0].time)
      if (Number.parseInt(response.data.trades[0].price, 10)>Number.parseInt(this.state.max, 10)){
        this.setState({ name_max: name })
        this.setState({ max: price })
      };
      if (Number.parseInt(response.data.trades[0].price, 10)<Number.parseInt(this.state.max, 10)){
        this.setState({ name_min: name })
        this.setState({ min: price })
      };

    })
    .catch((error) => {
      console.log('erroppppppp')
    })

    axios.get("https://api.binance.com/api/v3/ticker/price?symbol=BTCUSDT", {
        })
        .then(response => {
          var price= Number.parseInt(response.data.price, 10)
          var name = 'binance_bitcoin'

          if (price>Number.parseInt(this.state.max, 10)){
            this.setState({ name_max: name })
            this.setState({ max: price })
          };
          if (price<Number.parseInt(this.state.max, 10)){
            this.setState({ name_min: name })
            this.setState({ min: price })
          };
        })
        .catch(error => {
          console.log(error);
        });
    console.log("maxxxxx",this.state.max)
    console.log("minnnnnnnnnn",this.state.min)
  }
