import React from 'react';
import '../App.css';
import axios from 'axios'
import {withRouter} from 'react-router'

class List extends React.Component {
  constructor(props){
    super(props)
    this.state={
      bborderEmail: '1px solid #999',
      bborderPassword: '1px solid #999',
      hover: 'false',
      max:0,
      min: 0,
      name_max: null,
      name_min: null,
      nobitex_price: 0,
      nobitex_name: "nobitex bitcoin",
      exir_price: 0,
      exir_name: "exir bitcoin",
      binance_price: 0,
      binance_name:"binance bitcoin",
    }
  }

  componentDidMount () {
    this.interval = setInterval(() => {
      this.handleMax()
    }, 3000)
    }

  handleMax(){

    axios.get('https://api.exir.io/v1/trades', {
    })
      .then(response => {
        var price= Number.parseInt(response.data["btc-usdt"][0].price, 10)
        this.setState({ exir_price: price })
		})
      .catch(error => {
        console.log('error:')
      })

    let data = {
      symbol: "BTCUSDT"
    }

    axios.post('https://api.nobitex.ir/v2/trades', data,{})
    .then((response) => {
      var price= Number.parseInt(response.data.trades[0].price, 10)
      var name = 'nobitex_bitcoin'
      this.setState({ nobitex_: price })

    })
    .catch((error) => {
      console.log('erroppppppp')
    })

    axios.get("https://api.binance.com/api/v3/ticker/price?symbol=BTCUSDT", {
        })
        .then(response => {
          var price= Number.parseInt(response.data.price, 10)
          var name = 'binance_bitcoin'

          if (price>Number.parseInt(this.state.max, 10)){
            this.setState({ name_max: name })
            this.setState({ max: price })
          };
          if (price<Number.parseInt(this.state.max, 10)){
            this.setState({ name_min: name })
            this.setState({ min: price })
          };
        })
        .catch(error => {
          console.log(error);
        });
    console.log("maxxxxx",this.state.name_max,this.state.max)
    console.log("minnnnnnnnnn",this.state.name_min,this.state.min)
    var taghir=((this.state.max-this.state.min)/this.state.max)*100
    console.log("taghiraaaaaaaaaaat", taghir)
  }
  render(){
    return (
  		<span>aaaaaaaaaaaaaaa</span>
    );
  }
}
export default withRouter(List);
