import React from 'react';
import '../App.css';
import axios from 'axios'
import {withRouter} from 'react-router'

class List extends React.Component {
  constructor(props){
    super(props)
    this.state={
      bborderEmail: '1px solid #999',
      bborderPassword: '1px solid #999',
      hover: 'false',
      max:0,
      min: 1000000000000000,
      name_max: "nobitex bitcoin",
      name_min: "binance bitcoin",
      nobitex_price: 0,
      nobitex_name: "nobitex bitcoin",
      exir_price: 0,
      exir_name: "exir bitcoin",
      binance_price: 0,
      binance_name:"binance bitcoin",
    }
  }

  componentDidMount () {
    this.interval = setInterval(() => {
      this.handleMax()
      if(this.state.binance_price>this.nobitex_price){
        this.setState({ max: this.state.binance_price })
        this.setState({ name_max: this.state.binance_name })
        this.setState({ min: this.state.nobitex_price })
        this.setState({ name_max: this.state.binance_name })
      }
      if(this.state.exir_price>this.state.max){
        this.setState({ max: this.state.exir_price })
        this.setState({ name_max: this.state.exir_name })
      }
      if(this.state.exir_price < this.state.min){
        this.setState({ min: this.state.exir_price })
        this.setState({ name_min: this.state.exir_name })
      }
      console.log("ssssss",this.state.max)
      console.log("ssssss",this.state.min)
    }, 3000)
    }

  handleMax(){

    axios.get('https://api.exir.io/v1/trades', {
    })
      .then(response => {
        var price= Number.parseInt(response.data["btc-usdt"][0].price, 10)
        this.setState({ exir_price: price })
		})
      .catch(error => {
        console.log('error:')
      })

    let data = {
      symbol: "BTCUSDT"
    }

    axios.post('https://api.nobitex.ir/v2/trades', data,{})
    .then((response) => {
      var price= Number.parseInt(response.data.trades[0].price, 10)
      this.setState({ nobitex_price: price })

    })
    .catch((error) => {
      console.log('erroppppppp')
    })

    axios.get("https://api.binance.com/api/v3/ticker/price?symbol=BTCUSDT", {
        })
        .then(response => {
          var price= Number.parseInt(response.data.price, 10)
          this.setState({ binance_price: price })
        })
        .catch(error => {
          console.log(error);
        });
  }
  render(){
    return (
  		<span>aaaaaaaaaaaaaaa</span>
    );
  }
}
export default withRouter(List);
